#!/bin/bash

PORT=$@

EXPECTED=(''
    $''
    $''
    $''
    $''
    )

DATA=($'POST /write HTTP/1.1\r\nContent-Length: 1000\r\n\r\\r\r\r\r'
    $'POST /write HTTP/1.1\r\nContent-Length: 1000\r\n\r\\r\r\r\r'
    $'POST /write HTTP/1.1\r\nContent-Length: 1000\r\n\r\\r\r\r\r'
    $'POST /write HTTP/1.1\r\nContent-Length: 1000\r\n\r\\r\r\r\r'
    $'POST /write HTTP/1.1\r\nContent-Length: 1000\r\n\r\\r\r\r\r'
    )

send_write() {
    echo -n "${DATA[$1]}" | nc 127.0.0.1 $PORT &> /dev/null &
}

send_read() {
    REQUEST=$'GET /read HTTP/1.1\r\n\r\n'
    echo -n "$REQUEST" | nc 127.0.0.1 $PORT
}

send_write 0

wait

for i in $(seq 1000); do
    for i in $(seq 8); do
        N=$(( $RANDOM % 5 ))
        send_write $N
    done

    wait

    RES=`send_read | tail -n +4`

    for i in $(seq 0 4); do
        S="${EXPECTED[$i]}"
        if [ "$RES" = "$S" ]; then
            continue 2
        fi
    done

    echo "Did not find it: $RES"
    exit 1
done
